var NIGO=NIGO||{};NIGO.elements={portfolioItemLinks:".portfolio-item-link",overlay:".overlay"};NIGO.init=function(){var e=$(NIGO.elements.portfolioItemLinks);NIGO.showPortfolioContent(e)};NIGO.showPortfolioContent=function(e){for(var t=0;t<e.length;t++)(function(t){$(e[t]).on("click",function(e){e.preventDefault();var n=$(".item-"+t);$(n).removeClass("hidden");$(n).css("left",window.innerWidth/2-$(n).width()/2);$(NIGO.elements.overlay).removeClass("hidden").css("height",$(document).height()+"px");$(".overlay").on("click",function(){NIGO.hidePortfolioContent(".item-"+t)})})})(t)};NIGO.hidePortfolioContent=function(e){$(e).addClass("hidden");$(NIGO.elements.overlay).addClass("hidden")};NIGO.init();